(window.webpackJsonp=window.webpackJsonp||[]).push([[14],{458:function(t,s,v){"use strict";v.r(s);var a=v(2),_=Object(a.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("div",{staticClass:"custom-block tip"},[s("p",{staticClass:"title"}),s("p",[s("font",{attrs:{size:"5"}},[t._v("摘要")])],1),t._v(" "),s("p",[t._v("七八九，艰难的三个月。保研之旅告一段落，是时候开始下一阶段了。")])]),s("h2",{attrs:{id:"煎熬的七八月"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#煎熬的七八月"}},[t._v("#")]),t._v(" 煎熬的七八月")]),t._v(" "),s("p",[t._v("七月初学校出了前三学期的成绩排名。由于对学校保研政策的理解失误（选了一些无关大局但难拿高分的课程），导致成绩排名下降了好多（着实是卷，我这个分数段0.1分都能影响排名），顿时感觉保研有点悬了，于是放弃了参加各种夏令营（对自己的简历也没啥信心，无科研无竞赛低rank属实是个小趴菜），开始着手准备考研，想着复习专业课和数学课的知识顺便也是变相准备预推免。浏览网上关于考研的各种文章，感觉属实是太卷了，心里老惦记着能侥幸获得保研资格，心里没底的感觉真的煎熬。")]),t._v(" "),s("p",[t._v("八月底返校之后还要上软工实践课。第一节课要求分组，夏令营获得offer的大佬们选择抱团，剩下一群rookie们盯着我求组队（大家考研复习没有太多精力做实验，觉得我能保），一旁瑟瑟发抖的我只能无奈接手组长并几乎负责了项目80%的任务（这个项目经历后面面试阶段也帮了我很多，付出就有回报啊家人们），项目采用了vue+springboot的框架，还好之前有点基础，不然整出来属实费劲。")]),t._v(" "),s("h2",{attrs:{id:"挣扎的九月"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#挣扎的九月"}},[t._v("#")]),t._v(" 挣扎的九月")]),t._v(" "),s("p",[t._v("项目完成度很高，自我感觉良好，鉴定为优秀。恍恍惚惚到了九月中旬，令人期待的保研名单它来了来了。今年的保研率各院校都出奇的高，学院加上各种转专业的总计265人（不得不令人感叹计算机是多么的卷），保了51个，接近20%了，我综测排43（成绩排38），属于是个边缘人了😅。")]),t._v(" "),s("p",[t._v("末九无科研无竞赛边缘选手选择躺平。开摆！预推免先是投个本校试试水，心里想着本校总不至于不要我吧。没想到啊，还真不要，给发了个候补。（吐槽一下本校的复试属实没有含金量，自我介绍中英文+随机抽一道专业题目，根本没有区分度，瞅了眼本校预推免复试成绩，好多同校大佬分也不高）学院还整了一波骚操作属实是把👴给整无语了，没出成绩前发来个问卷询问是否接受调剂，我开始选择了“否”，思索一番后，为了保险起见，在截止时间前又修改成了“是”（再三确认！！），结果成绩出了没有给我调剂，最后给学校发邮件质疑，学校显然发现了问卷可能存在bug，为了省事（成绩提交后修改可能比较麻烦吧），直接把问卷记录删除了，并给回复说以导出的数据为准（心里一万个我测泥马），没有证据的我只能就此作罢😅。")]),t._v(" "),s("p",[t._v("之后又投了华南理工、大连理工和湖南大学，可能是投得比较晚，人家生源好，都招的差不多了，没给我面试资格。这波啊，这波属于是白给。")]),t._v(" "),s("p",[t._v("9月28日正式系统开放，早在0点守在电脑旁的我就填了个本校，期待能够候补上吧（之前咨询过学院老师，说候补大概率能补上）。令人感叹：大人啊，如今时代变了。估计是前两年各学校都给鸽出阴影了，今年超发offer+候补制度。9月28日一直等到下午我都没能等来候补通知，看到pyq各种晒上岸名校截图，稍感辛酸、凉凉，但觉得候补可能晚点才会发通知加上知道前几年正式推免存在较大的捡漏可能，心里还不是很慌。查看保研公众号（不吹不黑不打广告，保研派yyds），得知东南大学网安还有名额，晚上8点赶紧拉着室友跑到图书馆打印扫描相关资料，关心则乱，去的太急没事先填好基本信息表，属于是经典白跑一趟。忙到凌晨1点整理完相关资料，在推免系统报了名，还小小地拿室友资料复习了一波网络安全实验（伏笔）。")]),t._v(" "),s("p",[t._v("9月29日上午7点半起床，没咋睡好都，赶紧又跑去图书馆打印扫描，提交资料到老师邮箱。饿了，浅吃了一手mlt（属实是没啥胃口，就捡了一点菜还没吃完）。中午班主任找全班同学约谈，轮到我时，我给班主任汇报了我的情况，班主任还帮忙打电话给学校研招院问候补还有没有名额，最后得知学校名额都发完了，还被批评教育了一番。这下彻底寄了，感觉没学上咯😅。")]),t._v(" "),s("p",[t._v("下午1点半怀揣着悲凉的心情走回宿舍，半路中手机一响，顿感有要事发生，打开一看是东南大学给我发了复试通知。感觉是抓住了救命稻草啊，还就那个健步如飞，跑回宿舍，打开电脑查看确认接收。面试时间是下午两点，此时还剩20多分钟。时间有点紧张，闭上双眼开始默背自我介绍模板，告诉自己要沉重冷静，抓住仅有的机会，开冲！我应该是第一个面试的，复试进行地异常顺利，问的几乎都能回答上来（感谢室友昨晚提供的实验资料），自我感觉优秀，面试完感觉挺稳的（面试是在腾讯会议进行，不得不感慨东南大学的面试专业多了，包含线代、离散数学、计网、项目细节等，老师人也随和，面试体验很好），但不敢半场开香槟。战战兢兢过了两个半小时（期间听闻西电软件还有名额，但打了好几遍电话没人接，遂放弃挣扎），铭记16点58分这一时刻，极限上岸东南网安！！")]),t._v(" "),s("h2",{attrs:{id:"告别九月"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#告别九月"}},[t._v("#")]),t._v(" 告别九月")]),t._v(" "),s("p",[t._v("至此，我的保研之旅到此也就结束了，从躺平到至暗时刻再到惊险刺激的极限捡漏，属于是被幸运女神眷顾了。东南大学也算是梦校了，之前考研也打算冲东南的计科来着，只能说是双向奔赴了。最后，感谢洋带不收之恩😀！")]),t._v(" "),s("p",[t._v("今天是九月的最后一天，谨以此篇给自己的保研之旅做个告别，也给九月做个告别。是时候开始下一阶段了！")]),t._v(" "),s("p",[t._v("💪💪💪")])])}),[],!1,null,null,null);s.default=_.exports}}]);